int solve(Vec &a,Vec &b){
  queue<Vec> Q;
  queue<int> cnt;
  set<Vec> visited;
  Q.push(a);
  cnt.push(0);
  visited.insert(a);

  while(!Q.empty()){
    Vec v = Q.front(); Q.pop();
    int c = cnt.front(); cnt.pop();
    if(v == b) return c;
    for(int i = 0 ; i < N ; i++){
      for(int j = 1 ; j <= 9 ; j++){
