#define MAX_K 10

using namespace std;

struct dial{
	dial():k(0),depth(0){
		for(int i = 0; i < MAX_K; ++i)
			d[i] = -1;
	}
	int depth;
	int k;
	int d[MAX_K];
	int diff(const dial &t)const{
		int ret = 0;
		for(int i = 0; i < k; ++i){
			if( d[i] != t.d[i] )
				++ret;
		}
		return ret;
	}
};

int DialLock(const dial &init, const dial &unlock){
	int K = init.k;
	int ret = 0;
	queue<dial> qd;

	qd.push( init );
	while( !qd.empty() ){
		int diff = 0;
		dial now=qd.front();
		qd.pop();

		/*for(int i = 0; i < K; ++i){
			cout << now.d[i] << ' ';
		}
		cout << endl;*/

		diff = unlock.diff( now );
		if( diff == 0 ){
			ret = now.depth;
			break;
		}

		for(int i = 0; i < K; ++i){
			for(int k = 0; k < MAX_K; ++k){
				for(int l = 1; l <= K - i; ++l){
					int diff_t = 0;
					dial t = now;
					for(int m = 0; m < l; ++m){
						t.d[i+m]=(now.d[i+m]+k)%MAX_K;	
					}
					diff_t = unlock.diff( t );
					if( diff > diff_t ){
						++t.depth;
						qd.push( t );
					}
				}
			}
		}
	}

	return ret;
}

int main(){
	while( true ){
		int k;
		string a,b;
		dial init,unlock;

		cin >> k;
		if( k == 0 )
			break;
		cin >> a >> b;
		init.k = unlock.k = a.length();
		for(unsigned int i = 0; i < a.length(); ++i){
			init.d[i] = a[i] - '0';
			unlock.d[i] = b[i] - '0';
		}
		init.depth=0;

		cout << DialLock( init, unlock ) << endl;
	}
	return 0;
}

