string rot(int p, int n, string d)
{
	string ret=d;

	int t=(ret[p]-'0')+n;
	if(t<0) t+=10;
	ret[p]=(t%10)+'0';

	return ret;
}

bool dfs(string rt, int p, int c)
{
	if(p==K) return true;

	int m;
	while(1)
	{
		m=(dis[p]-rt[p]);
