};

vi ask(vi num) {
	vi res;
	int a = -1;
	REP(i, num.size()) {
		if (num[i]!=a) {
			res.push_back(num[i]);
		}
		a = num[i];
	}

	while (!(res.size() == 1 && res[0] == 0) && res[res.size() - 1] == 0)
		res.erase(res.begin() + res.size() - 1);
	return res;
}

