class State{
public:
  string str;
  int cnt;
};

int solve(string s, string t){
  State u, v;
  u.str = s;
  u.cnt = 0;
  queue<State> Q;
  Q.push(u);
  int dist[10];
  while(!Q.empty()){
    u = Q.front(); Q.pop();
    if(u.str == t){
      return u.cnt;
    }
    //cout << u.str << ' ' << u.cnt << endl;
    v.cnt = u.cnt + 1;
    for(int i=0;i<u.str.size();i++){
      int num = t[i]-u.str[i];
      if(num<0) num+=10;
      dist[i] = num;
    }
    /*for(int i=0;i<u.str.size();i++){
      cout << dist[i] << ' ';
    }
    cout << endl;*/
    int l, r;
    for(int i=0;i<u.str.size();i++){
      if(dist[i]!=0){
	l = i;
	break;
      }
    }
    for(int i=(int)u.str.size()-1;i>=0;i--){
      if(dist[i]!=0){
	r = i;
	break;
      }
    }
    if(l>r) continue;
    if(dist[l] == dist[r]){
      v.str = u.str;
      for(int i=l;i<=r;i++){
	int num = u.str[i]-'0' + dist[l];
	if(num>=10) num-=10;
	v.str[i] = '0' + num;
      }
      Q.push(v);
    }else{
      //cout << l << ' ' << r << endl;
      int l2 = 0, r2 = u.str.size();
      //left
      for(int i=l;i<u.str.size();i++){
	if(dist[i] != dist[l]){
	  r2 = i;
	  break;
	}
      }
      v.str = u.str;
      for(int i=l;i<r2;i++){
	int num = u.str[i]-'0' + dist[l];
	if(num>=10) num-=10;
	v.str[i] = '0' + num;
      }
      Q.push(v);
      //right
      for(int i=r;i>=0;i--){
	if(dist[i] != dist[r]){
	  l2 = i;
	  break;
	}
      }
      v.str = u.str;
      for(int i=r;i>l2;i--){
	int num = u.str[i]-'0' + dist[r];
	if(num>=10) num-=10;
	v.str[i] = '0' + num;
      }
      Q.push(v);
    }
    //return -1;
  }
  return -1;
}

