int search(int *rot, int t, int cost){
	if(t == N) return cost;
	if(!rot[t]) return search(rot, t+1, cost);
	int rn = rot[t];
	int min = 10;
	for(int i=t;i<N;i++){
		int tmp[12];
		memcpy(tmp, rot, sizeof(tmp));
		for(int j=t;j<=i;j++) tmp[j]=(tmp[j]-rn+10)%10;
		int ret = search(tmp, t+1, cost+1);
		if(ret<min)min=ret;
	}
	return min;
}

