                rep(i,d.size()) if(d[i] == 0) u[i] = true;
                rep(i,d.size()){
                    if(u[i]) continue;
                    m[ d[i] ]++;
                    if(maxi < m[ d[i] ]){
                        maxi = m[ d[i] ];
                        p = d[i];
                    }
                }
                rep(i,d.size()){
                    if(u[i]) continue;
                    if(m[ d[i] ] != maxi) cnt++;
                }
                cnt++;
                break;
            }
        }

        cout << cnt << endl;
    }
}

