class state{
public:
  int now,n,cnt;
  vector<int> data;
  bool operator<(const state & a)const{
    rep(i,n)if ( data[i] !=a.data[i])return data[i] < a.data[i];
    return false;
  }
  bool solved(vector<int> &tar){
    rep(i,tar.size()){
      if ( data[i] != tar[i])return false;
    }

    return true;
  }

  bool move(set<state>&S,queue<state> &Q,vector<int> &tar){
    while(now < n && data[now] == tar[now])now++;
    if ( now == n){
      if(  solved(tar))return true;
      else return false;
    }
    int move=tar[now]-data[now];
    data[now]=(10+data[now]+move)%10;
    cnt++;
    if ( S.find(*this)== S.end())S.insert(*this),Q.push(*this);
    for(int i=now+1;i<n;i++){
      data[i]=(10+data[i]+move)%10;
      if ( S.find(*this)==S.end())S.insert(*this),Q.push(*this);
    }

    return false;
  }

};

int solve(state & ini,vector<int> tar){
  set<state> S;
  queue<state> Q;
  Q.push(ini);
  S.insert(ini);
  while(!Q.empty()){
    state now = Q.front();

    Q.pop();
    if ( now.solved(tar) )return now.cnt;
    if ( now.move(S,Q,tar))return now.cnt;
  }
  return -1;
}

main(){
  int n;
  string a;
  while(cin>>n && n){
    state ini;
