  if(k==i) return 0;

  //add
  int t = (dif[i]+1000-sum_add)%10;
  if(t==0){
    ret = min(ret,dfs(k,i+1,dif,sum_add,add,add_size));
  }else{
    add[add_size++] = t;
    ret = min(ret,dfs(k,i+1,dif,sum_add+t,add,add_size)+1);
    add_size--;
  }

  //rm
