int deleteNumver(vector<int> &d){
    map<int,int> m;
    rep(i,d.size()){
        m[d[i]]++;
    }

    int res = 0;
    if(m[ d.front() ] == 1) {d.erase(d.begin()); res++; }
    if(m[ d.back() ] == 1) {d.erase(--d.end()); res++; };

    return res;
}

vector<int> deleteRow(vector<int> d){
    vector<int> res;
    rep(i,d.size()){
        if(res.empty()) res.emplace_back(d[i]);
        else if(res.back() != d[i]) res.emplace_back(d[i]);
    }
    return res;
}

vector<int> deleteZero(vector<int> d){
    vector<int> ret;
    bool u[11] = {0};

    rep(i,d.size()){
        if(d[i] == 0) u[i] = true;
        else break;
    }

    for(int i = d.size() - 1; i >= 0; i--){
        if(d[i] == 0) u[i] = true;
        else break;
    }

    rep(i,d.size()){
        if(not u[i]) ret.emplace_back(d[i]);
    }
    return ret;
}

