int f(const vector<int>& v, int i) {
  int n = v.size();
  if (i == n) return 0;
  int ml = 100;
  int k = (10 - v[i]) % 10;
  if (k == 0) return f(v, i+1);
  for (int j=i; j<n; ++j) {
    auto cp = v;
    for (int l=i; l<=j; ++l) {
      cp[l] += k;
      cp[l] %= 10;
    }
    ml = min(ml, f(cp, i+1) + 1);
  }
  return ml;
}

