int men;

void dfs(vector<int> &words,vector<int> &target,int cost,int pos,int k)
{
  if(men <= cost)
    return;
  if(pos >= k)
    {
      men = min(men,cost);
      return;
    }

  if(target[pos] == words[pos])
    {
      dfs(words,target,cost,pos+1,k);
      return;
    }

  for(int i=pos;i<k;i++)
    {
      int minus = target[pos]-words[pos];
      vector<int> prev = words;
      for(int j=pos;j<=i;j++)
	{
	  words[j] = (words[j]+minus);
	  if(words[j] < 0)words[j]+=10;
	  words[j] %= 10;
	}
      dfs(words,target,cost+1,pos+1,k);
      words = prev;
    }

}

