      while(a[L] == 0 && L <= R)L++;
      while(a[R] == 0 && L <= R)R--;
      if(L > R) break;
      int left_count = 0, right_count = 0;
      for(int i=L; i<R+1; i++){
	if(a[i] == a[L])left_count++;
	if(a[i] == a[R])right_count++;
      }

      int sub = 0;
      int c = -1;
      int cc = 0;
      for(int i=L; i<R+1; i++){
