	if(c > m[a[i]]){
	  c = m[a[i]];
	  cc = a[i];
	}

      }     

      if(a[L] != a[R]){
	if(c > left_count && c > right_count){
	  sub = cc;
	}
      }

      if(cc == 0){
	if(left_count >= right_count) sub = a[L];
	else sub = a[R];
      }

      for(int i=L; i<R+1; i++){
	if(a[i] >= sub) a[i] -= sub;
	else a[i] = 10 - (sub-a[i]);
      }

      ans++;
    }    
    int res = 1<<29;
    for(int i=0; i<v.size(); i++) res = min(res,v[i]);
    ans = min(ans,res);
