int n;
int a[20], b[20];
int dp[20][20][10];

bool check(int l, int r, int k)
{
	for (int i = l; i < r; i++){
		if ((a[i] + k) % 10 != b[i]) return false;
	}
	return true;
}

int calc(int l, int r, int k)
{
	if (~dp[l][r][k]) return dp[l][r][k];
	if (check(l, r, k)) return dp[l][r][k] = 0;
	int res = 99999999;
	for (int m = l + 1; m < r; m++){
		int vl = 99999999;
		for (int i = 0; i < 10; i++){
			vl = min(vl, !i + calc(l, m, (k + i) % 10));
		}
		int vr = 99999999;
		for (int i = 0; i < 10; i++){
			vr = min(vr, !!i + calc(m, r, (k + i) % 10));
		}
		res = min(res, vl + vr);
	}
	return dp[l][r][k] = res;
}

