		string s, t;
		cin >> s >> t;
		for (int i = 0; i < n; i++){
			a[i] = s[i] - '0';
			b[i] = t[i] - '0';
		}
		memset(dp, -1, sizeof(dp));
		int res = 99999999;
		for (int i = 0; i < 10; i++){
			res = min(res, !!i + calc(0, n, i));
		}
		cout << res << endl;
	}
}

