int solve(vector<int> D, vector<int> imos, int i, int d){
  int n = D.size();
  if(i == n) return 0;
  d += imos[i];
  D[i] += 10 - d;
  D[i] %= 10;
  int ret = 1e9;
  if(D[i]){
    for(int j = i+1; j <= n; ++j){
      imos[j] -= D[i];
      ret = min(ret, 1 + solve(D,imos,i+1,(d+D[i])%10));
      imos[j] += D[i];
    }
  }else{
    ret = min(ret,solve(D,imos,i+1,d));
  }
  return ret;
}

