llint dfs(llint p)
{
	if(p >= n) return 0;

	if(d[p] == 0) return dfs(p+1);

	llint ret = inf;
	for(int i = p+1; i <= n; i++){
		llint org = d[i];
		d[i] += d[p], d[i] %= 10;
		ret = min(ret, dfs(p+1));
		d[i] = org;
	}
	ret++;
	return ret;
}

