map<string,int> memo;

string rot_n(string s,int start,int len,int rot) {
    for(int i=0; i<len; ++i) {
        s[start+i] -= rot;
        if(s[start+i] < '0') s[start+i] += 10;
        if(s[start+i] > '9') s[start+i] -= 10;
    }
    return s;
}

int dfs(string s,int c,string t) {
    //cout<<s<<" "<<c<<" "<<t<<endl;
    if(memo.find(s) != memo.end()) return memo[s];
    if(s[c] == t[c]) return memo[s] = dfs(s,c+1,t)+1;
    int res = 1<<29;
    for(int i=1; i<=(s.length()-c); ++i) {
        res = min(res, dfs(rot_n(s,c,i,s[c]-t[c]),c+1,t) + 1);
    }
    return memo[s] = res;
}

