void cul(vector<vector<vector<ll>>> &dp,ll k,string s){
    for(int i=0;i<k;i++){
        for(int t=i;t<=k;t++){
            for(int h=0;h<10;h++){dp[i][t][h]=E;}
        }
    }
    for(int i=0;i<=k;i++){
        for(int h=0;h<10;h++){dp[i][i][h]=0;}
    }
    priority_queue<pair<pll,pll>,vector<pair<pll,pll>>,greater<pair<pll,pll>>> Q;
    for(int i=0;i<k;i++){
        dp[i][i+1][s[i]]=0;
        Q.push({{0,i},{i+1,s[i]}});
    }
    while(!Q.empty()){
        pair<pll,pll> w=Q.top(); Q.pop();
        if(dp[w.F.S][w.S.F][w.S.S]!=w.F.F){continue;}
        for(int h=1;h<10;h++){
            if(dp[w.F.S][w.S.F][(w.S.S+h)%10]>w.F.F+1){
                dp[w.F.S][w.S.F][(w.S.S+h)%10]=w.F.F+1;
                Q.push({{w.F.F+1,w.F.S},{w.S.F,(w.S.S+h)%10}});
            }
        }
        for(ll l=0;l<=w.F.S;l++){
            for(ll r=w.S.F;r<=k;r++){
                if(dp[l][r][w.S.S]>dp[l][w.F.S][w.S.S]+w.F.F+dp[w.S.F][r][w.S.S]){
                    dp[l][r][w.S.S]=dp[l][w.F.S][w.S.S]+w.F.F+dp[w.S.F][r][w.S.S];
                    Q.push({{dp[l][r][w.S.S],l},{r,w.S.S}});
                }
            }
        }
    }
}


