string rotate(const string& str ,int offset,int turn_count,int mask_interval){
  int base_mask = (1<<mask_interval) - 1;
  int current_mask = (base_mask << offset);

  string next_dial="";
  for(int i=0;i<str.size();i++){
    if((current_mask >> i) & 1){
      int next_num = (str[i] - '0' + turn_count) % 10;
      next_dial.push_back('0' + next_num);
    }
    else{
      next_dial.push_back(str[i]);
    }
  }
  return next_dial;
}

class State{
public:
  string dial;
  int cost;
  State(string _d,int _c) : dial(_d),cost(_c) {}
  State() : dial(""),cost(0) {}

  bool operator <(const State& s) const{
    return cost < s.cost;
  }

  bool operator >(const State& s) const{
    return cost > s.cost;
  }
};

int compute_dist(int lhs,int rhs){
  for(int i=0;i<=10;i++){
    if((lhs + i) % 10 == rhs){
      return i;
    }
  }
  return 0;
}

int main(){
  int total_dials;
  while(~scanf("%d",&total_dials)){
    if(total_dials == 0) break;

    string initial_state,unlocked_state;
    cin >> initial_state >> unlocked_state;
    map<string,int> dp;
    map<string,int> dp_from_back;

    dp[initial_state] = 0;

    priority_queue<State,vector<State>,greater<State> > que;
    que.push(State(initial_state,0));

    while(!que.empty()){
      State s = que.top();
      que.pop();

      for(int dial_idx = 0; dial_idx < s.dial.size(); dial_idx++){
	if(s.dial[dial_idx] == unlocked_state[dial_idx]) continue;

	int turn_count = compute_dist(s.dial[dial_idx] - '0',unlocked_state[dial_idx] - '0');
	for(int mask_interval = 1; dial_idx + mask_interval <= s.dial.size(); mask_interval++){
	  string candidate = rotate(s.dial,dial_idx,turn_count,mask_interval);
	  if(dp.find(candidate) == dp.end()){
	    dp[candidate] = s.cost + 1;
	    if(s.cost + 1 >= 5) continue;
	    que.push(State(candidate,s.cost + 1));
	  }
	}
      }
    }

    que.push(State(unlocked_state,0));
    while(!que.empty()){
      State s = que.top();
      que.pop();

      for(int dial_idx = 0; dial_idx < s.dial.size(); dial_idx++){
	if(s.dial[dial_idx] == initial_state[dial_idx]) continue;

	int turn_count = compute_dist(s.dial[dial_idx] - '0',initial_state[dial_idx] - '0');
	for(int mask_interval = 1; dial_idx + mask_interval <= s.dial.size(); mask_interval++){
	  string candidate = rotate(s.dial,dial_idx,turn_count,mask_interval);
	  if(dp_from_back.find(candidate) == dp_from_back.end()){
	    dp_from_back[candidate] = s.cost + 1;

	    if(s.cost + 1 >= 5) continue;
	    que.push(State(candidate,s.cost + 1));
	  }
	}
      }
    }

    int res = 0;
    if(dp.find(unlocked_state) != dp.end()){
      res = dp[unlocked_state];
    }
    else{
      for(map<string,int>::iterator it = dp.begin();
	  it != dp.end();
	  it++){
	if(dp_from_back.find(it->first) != dp_from_back.end()){
	  res = it->second + dp_from_back[it->first];
	  break;
	}
      }
    }

    printf("%d\n",res);
  }
}

