using namespace std;

int N;

int solve(string &s,string &t){
  queue<string> Q;
  map<string,int> dist;
  Q.push(s);
  dist[s] = 1;
  while(!Q.empty()){
    string v = Q.front(); Q.pop();
    if(v == t) return --dist[v];
    if(dist[v] == N) continue;
    for(int i = 0 ; i < N ; i++){
      for(int j = 1 ; j <= 9 ; j++){
        string next = v;
        for(int k = i ; k < N ; k++){
          next[k] = ((next[k]-'0' + j) % 10) + '0';
          if(!dist[next]){
            dist[next] = dist[v] + 1;
            Q.push(next);
          }
        }
      }
    }
  }
}

int main(){
  string s,t;
  while(cin >> N, N){
    cin >> s >> t;
    cout << solve(s,t) << endl;
  }
  return 0;
}

