#define REP(i, n) for(int i=0; i<n; i++)
#define rREP(i, s, n) for(int i=s; i<n; i++)
typedef pair<int, int> pii;

int k;
string a, b;
int s[10], g[10];
int c;
bool locked[10];

int main(){
    while(1){
        cin >> k;
        if(k==0) break;

        cin >> a >> b;
        REP(i, k){
            s[i] = a[i] - '0';
            g[i] = b[i] - '0';
        }

        c = 0;
        REP(i, k) locked[i] = true;
        queue<pii> que;
        que.push(pii(0, k));
        while(!que.empty()){
            pii p = que.front(); que.pop();
            c += 1;
            while(1){
                rREP(i, p.first, p.second){
                    s[i] = (s[i]+1) % 10;
                }
                bool changed = false;
                rREP(i, p.first, p.second){
                    if(s[i] == g[i]){
                        locked[i] = false;
                        changed = true;
                    }
                }
                if(changed){
                    bool cor = false;
                    int f;
                    rREP(i, p.first, p.second){
                        if(!cor && locked[i]){
                            f = i; cor=true;
                        }
                        if(cor && !locked[i]){
                            que.push(pii(f, i));
                            cor = false;
                        }
                        if(cor && i==p.second-1){
                            que.push(pii(f, p.second));
                        }
                    }
                    break;
                }
            }
        }
        cout << c << endl;
    }
}

