using namespace std;

class State{
public:
  string str;
  int cnt;
};

int solve(string s, string t){
  State u, v;
  u.str = s;
  u.cnt = 0;
  queue<State> Q;
  Q.push(u);
  int dist[10];
  bool match[10];
  while(!Q.empty()){
    u = Q.front(); Q.pop();
    if(u.str == t){
      return u.cnt;
    }
    //cout << u.str << ' ' << u.cnt << endl;
    v.cnt = u.cnt + 1;
    for(int i=0;i<u.str.size();i++){
      if(u.str[i] == t[i]) match[i] = true;
      else match[i] = false;
      int num = t[i]-u.str[i];
      if(num<0) num+=10;
      dist[i] = num;
    }
    /*for(int i=0;i<size;i++){
      cout << dist[i] << ' ';
    }
    cout << endl;*/
    int l, r;
    for(int i=0;i<u.str.size();i++){
      if(!match[i]){
	l = i;
	break;
      }
    }
    for(int i=(int)u.str.size()-1;i>=0;i--){
      if(!match[i]){
	r = i;
	break;
      }
    }
    if(l<=r){
      //cout << l << ' ' << r << endl;
      v.str = u.str;
      for(int i=l;i<=r;i++){
	int num = u.str[i]-'0' + dist[l];
	if(num >= 10) num -= 10;
	v.str[i] = '0' + num;
      }
      //cout << v.str << ' ' << v.cnt << ' ';
      Q.push(v);
      v.str = u.str;
      for(int i=l;i<=r;i++){
	int num = u.str[i]-'0' + dist[r];
	if(num >= 10) num -= 10;
	v.str[i] = '0' + num;
      }
      //cout << v.str << ' ' << v.cnt << endl;
      Q.push(v);
    }
    //return cnt;
  }
  return -1;
}

main(){
  int n;
  while(cin >> n){
    if(n == 0) break;
    string a, b;
    cin >> a >> b;
    cout << solve(a, b) << endl;
  }
  return 0;
}

#include <bits/stdc++.h>

using namespace std;

int N;

int solve(string &s,string &b){
  queue<string> Q;
  queue<int> cnt;
  set<string> visited;
  Q.push(s);
  cnt.push(0);
  visited.insert(s);

  while(!Q.empty()){
    string v = Q.front(); Q.pop();
    int c = cnt.front(); cnt.pop();
    if(v == b) return c;
    for(int i = 0 ; i < N ; i++){
      for(int j = 1 ; j <= 9 ; j++){
        string next = v;
        for(int k = i ; k < N ; k++){
          next[k] = ((next[k]-'0' + j) % 10) + '0';
          if(!visited.count(next)){
            visited.insert(next);
            Q.push(next);
            cnt.push(c+1);
          }
        }
      }
    }
  }
  return -1;
}

int main(){
  string s,t;
  while(cin >> N, N){
    cin >> s >> t;
    cout << solve(s,t) << endl;
  }
