ll solve(ll k){
    ll res = INF;
    string S,T; cin >> S >> T;
    map<string,int> mp;
    queue<string> q;
    mp[S] = 0;
    q.push(S);
    while(true){
        ll qsize = (int)q.size();
        while(qsize--){
            string s = q.front(); q.pop();
            int cnt = mp[s];
            for(int i = 0; i < k;i++){
                if(s[i] == T[i]) continue;
                ll kaiten = (T[i] - S[i] + 10)%10;
                string next = s;
                for(int j = 0; j < k;j++){
                    next[(i+j)%k] = (s[(i+j)%k] - '0' + kaiten)%10 + '0';
                    if(mp.find(next) == mp.end()){
                        mp[next] = cnt + 1;
                        q.push(next);
                    }
                    if(next == T) return res = cnt+1;
                }
            }
        }
    }
    return res;
}
