using namespace std;

int k;
int data[2][11];
int dp[21][21][10];
int res;

int func(int f,int t,int c){
	if(dp[f][t][c]!=INF)return dp[f][t][c];
	if(f==t)return 0;
	if((data[0][f%k]+c)%10==data[1][f%k])return dp[f][t][c]=func(f+1,t,c);
	if((data[0][(t-1)%k]+c)%10==data[1][(t-1)%k])return dp[f][t][c]=func(f,t-1,c);
	int res=INF;
	for(int i=1;i<=9;i++){
		int nc=(c+i)%10;
		int nf=f,nt=t;
		while(nf<nt && (data[0][nf%k]+nc)%10==data[1][nf%k])nf++;
		while(nf<nt && (data[0][(nt-1)%k]+nc)%10==data[1][(nt-1)%k])nt--;
		if(nf!=f || nt!=t)res=min(res,func(nf,nt,nc));
	}
