vector<string> rotate(const string& str ,int turn_count,int mask_interval){
  vector<string> res;

  int base_mask = (1<<mask_interval) - 1;
  for(int offset=0;offset+mask_interval<=str.size();offset++){
    int current_mask = (base_mask << offset);

    string next_dial="";
    for(int i=0;i<str.size();i++){
      if((current_mask >> i) & 1){
	int next_num = (str[i] - '0' + turn_count) % 10;
	next_dial.push_back('0' + next_num);
      }
      else{
	next_dial.push_back(str[i]);
      }
    }
    res.push_back(next_dial);
  }

  return res;
}

class State{
public:
  string dial;
  int cost;
  State(string _d,int _c) : dial(_d),cost(_c) {}
  State() : dial(""),cost(0) {}

  bool operator <(const State& s) const{
    return cost < s.cost;
  }

  bool operator >(const State& s) const{
    return cost > s.cost;
  }
};

int compute_dist(int lhs,int rhs){
  for(int dist=1; dist <= 9; dist++){

    //ex... lhs:1 rhs:9
    if((lhs + dist) % 10 == rhs){
      //ex... lhs:1 rhs:9 -> dist:8 -> don't return val
      //turn lhs dial +8
      return dist;
    }
    if((rhs + dist) % 10 == lhs){
      //ex... lhs:1 rhs:9 -> dist:-2 -> return val -2
      //turn lhs dial -2
      return -dist;
    }
  }
  return 0;
}

