typedef long long ll;

ll s2ll(const string &s){
	ll x = 0;
	for(int i = 0; i < s.size(); ++i){
		x = (x << 4) | (s[i] - '0');
	}
	return x;
}

ll make_mask(int left, int right){
	ll m = 0x1111111111111111ll;
	m &= ~((1ll << (left << 2)) - 1);
	m &= ((1ll << (right << 2)) - 1);
	return m;
}

ll rotate(int k, ll x, ll m){
	x += m;
	ll c = ((x & (x >> 2)) >> 1) & 0x1111111111111111ll;
	x &= ~(c | (c << 1) | (c << 2) | (c << 3));
	x &= (1ll << (k << 2)) - 1;
	return x;
}

int dfs(int k, ll s, ll g){
	if(k == 0){ return 0; }
	if((s & 15) == (g & 15)){ return dfs(k - 1, s >> 4, g >> 4); }
	const ll m = 0x1111111111111111ll;
	ll t = s;
	while((t & 15) != (g & 15)){ t = rotate(k, t, m); }
	int answer = k;
	for(int i = 1; i <= k; ++i){
		ll n = (1ll << (i << 2)) - 1;
		answer = min(answer, dfs(k - 1, ((s & ~n) | (t & n)) >> 4, g >> 4));
	}
	return answer + 1;
}

