void recursive(char buf[11],int loc,int count){

	if(count >= minimum)return;

	bool FLG = true;
	for(int i = 0; i < K; i++){
		if(buf[i] != ans[i]){
			FLG = false;
			break;
		}
	}
	if(FLG){
		minimum = min(minimum,count);
		return;
	}

	int diff;

	if(ans[loc] == buf[loc]){
		diff = 0;
	}else if(ans[loc] < buf[loc]){
		diff = ans[loc] + 10 - buf[loc];
		count++;
	}else{
		diff = ans[loc] - buf[loc];
		count++;
	}

	for(int right = loc; right < K; right++){
		char next[11];

		for(int i = 0; i < loc; i++){
			next[i] = buf[i];
		}
		for(int i = loc; i <= right; i++){
			next[i] = '0' + calc(buf[i]-'0',diff);
		}
		for(int i = right+1; i < K; i++){
			next[i] = buf[i];
		}
		recursive(next,loc+1,count);
	}
}

