ll solve(ll k){
    ll res = INF;
    string S,T; cin >> S >> T;
    for(int i = 0; i < k;i++){
        ll cnt = 0;
        ll kaitensuu = T[i] - S[i];
        if(kaitensuu < 0) kaitensuu += 10;
        if(kaitensuu != 0) cnt++;
        queue<pll> q; q.push({kaitensuu,cnt});
        for(int j = i; j < i+k; j++){

            ll qsize = (ll)q.size();
            while(qsize--){
                auto p = q.front(); q.pop();
                ll kai = p.first, c = p.second;

                if((S[j%k] + kai - '0')%10 == (T[j%k] - '0')%10){
                    q.push({kai,c});
                }else{
                    if(T[j%k] == S[j%k]){
                        q.push({0,c});
                    }
                    ll kai2 = T[j%k] - S[j%k];
                    if(kai2 < 0) kai2 += 10*100;
                    kai2%=10;
                    q.push({kai2,c+1});
                    kai += (T[j%k] + kai) - S[j%k];
                    if(kai < 0) kai += 10*100;
                    kai%=10;
                    q.push({kai,c+1});
                }
            }
        }
        while(q.size()){
            res = min(res,q.front().second);
            q.pop();
        }
    }
    return res;
}
