int DialLock(int K, const dial &init, const dial &unlock){
	int ret = 0;
	queue<dial> qd;

	qd.push( init );
	while( !qd.empty() ){
		dial now=qd.front();
		qd.pop();

		/*
		for(int i = 0; i < K; ++i){
			cout << now.d[i] << ' ';
		}
		cout << endl;
		*/

		if( 0 == unlock.diff( now ) ){
			ret = now.depth;
			break;
		}

		for(int i = 0; i < K; ++i){
			int rot;
			if( now.d[i] != unlock.d[i] ){
				dial t = now;

				++t.depth;
				rot = unlock.d[i] - now.d[i];
				rot += (rot<0?MAX_K:0);

				for(int m = 0; m < K - i; ++m){
					t.d[i+m]=(now.d[i+m]+rot)%MAX_K;
					qd.push( t );
				}

				break;
			}
		}
	}

	return ret;
}

