struct Comp {
	bool operator() (pni a, pni b) {
		return a.second > b.second;
	}
};

string rot(string a, int from, int to, int n)
{
	rep(i, from, to + 1)
	{
		int j = a[i] - '0';
		j = (j + n + 10) % 10;
		a[i] = '0' + j;
	}

	return a;
}

int diff(string a, string b)
{
	int ret = 0;
	rep(i, 0, a.length())
	{
		int aa = a[i] - '0';
		int bb = b[i] - '0';
		ret += abs(aa - bb);
	}
	return ret;
}

int solve(int k)
{
	string start, end;
	cin >> start >> end;

	priority_queue<pni, vector<pni>, Comp> que;
	map<Node, int> dist;
	set<Node> done;

	que.push(pni(start, 0));
	while (!que.empty())
	{
		Node n = que.top().first;
		int c = que.top().second;
		que.pop();

		if (done.find(n) != done.end()) continue;
		done.insert(n);

		if (n == end) return c;

		int now_diff = diff(n, end);

		rep(i, 0, k) rep(j, i, k) rep(jj, -9, 10)
		{
			if (jj == 0) continue;
			string nnode = rot(n, i, j, jj);
			int next_diff = diff(nnode, end);
			if (now_diff < next_diff) continue;

			int nc = c + 1;
			if (dist.find(nnode) == dist.end()) dist[nnode] = INF;
			if (nc < dist[nnode])
			{
				dist[nnode] = nc;
				que.push(pni(nnode, nc));
			}
		}
	}
}

