            if((S[j%k] + kaitensuu - '0')%10 == (T[j%k] - '0')%10) continue;
            kaitensuu = T[j%k] - S[i%k];
            if(kaitensuu != 0) cnt++;
        }
        res = min(res,cnt);
    }
    return res;
}
int main(void) {
    cin.tie(0); ios_base::sync_with_stdio(false);
    ll k;
    while(cin >> k,k){
        cout << solve(k) << endl;
    }
    return 0;
}

