typedef long long int lli;

lli pow10(int n){
    lli ret=1;
    for(int i=0; i<n; i++) ret*=10;
    return ret;
}

lli change_dig(lli n, int d, int f){
    lli num = (n/pow10(d))%10;
    return n -num*pow10(d) +((f+num)%10)*pow10(d);
}

lli change(lli n, int b, int e, int f){
    for(int i=b; i<=e; i++){
        n = change_dig(n, i, f);
    }
    return n;
}

int main(){
    while(1){
        int n;
        cin >> n;
        if(n==0) break;

        lli d1,d2;
        cin >> d1 >> d2;
        map<lli, int> dp;
        dp[d1] = 0;
        for(int i=0; i<n; i++){
            map<lli, int> tmp;
            for(map<lli, int>::iterator itr=dp.begin(); itr!=dp.end(); itr++){
                int f = ( (d2/pow10(i))%10 -(itr->first/pow10(i))%10 +10 )%10;
                if(f==0){
                    tmp[itr->first] = itr->second;
                    continue;
                }
                for(int j=i; j<n; j++){
                    lli num = change(itr->first, i, j, f);
                    if(tmp.count(num)==0){
                        tmp[num] = itr->second +1;
                    }else if(tmp[num] > itr->second +1){
                        tmp[num] = itr->second +1;
                    }
                }
            }
            dp = tmp;
        }
        cout << dp[d2] << endl;
    }
