    ll ret=0;
    for(auto e : v){
        ret=10*ret+e;
    }
    return ret;
}
vector<int> ltov(ll x,int n){
    vector<int> ret(n);
    rep(i,n){
        ret[n-i-1]=x%10;
        x/=10;
    }
    return ret;
}

