};

int fix(int a){
  if(a >= 10) a -= 10;
  if(a < 0) a += 10;
  return a;
}

void bfs(){
  queue<state> Q;
  state u(v);

  for(Q.push(u); !Q.empty();){
    u = Q.front();
    Q.pop();

    for(int i=u.n;i<n;i++){
      if(u.v[i] == 0) u.n++;
      else break;
