    int cnt = u.v[u.n];
    for(int i=u.n;i<n;i++){
      u.v[i] = fix(u.v[i] - cnt);
      Q.push(state(u.v, u.n+1, u.ans+1));
    }
  }

  cout << n << endl;
}

