void turn(int& x,int k)
{
	x+=k;
	while(x<0) x+=10;
	while(x>9) x-=10;
}

int solve(vector<int> a,vector<int> b,int i)
{
	int n=a.size();
	if(i==n) return a[i-1]!=b[i-1];

	if(i>0 && a[i-1]!=b[i-1]){
		int d=b[i-1]-a[i-1];
		turn(a[i-1],d);
		int res=1+solve(a,b,i+1);
		for(int j=i;j<n;j++){
			turn(a[j],d);
			res=min(res,1+solve(a,b,i+1));
		}
		return res;
	}
	else
		return solve(a,b,i+1);
}

