#include <cstdlib>
#include <iostream>
#include <vector>
using namespace std;

const int mod = 10;
int k;
int ans;

void calc(const int idx, vector<int> rests, const int sum) {
	if(idx == k) {
		ans = sum;
		return;
	}
	if(rests[idx] == 0) {
		calc(idx + 1, rests, sum);
		return;
	}

	const int next_sum = sum + 1;
	if(next_sum >= ans) return;
	const int next_idx = idx + 1;

	calc(next_idx, rests, next_sum);
	for(int i = idx + 1; i < k; ++i) {
		rests[i] = (rests[i] - rests[idx] + mod) % mod;
		calc(next_idx, rests, next_sum);
	}
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);

	while(cin >> k && k) {
		string initial, unlock;
		cin >> initial >> unlock;

		vector<int> rests(k);
		for(int i = 0; i < k; ++i) {
			rests[i] = (unlock[i] - initial[i] + mod) % mod;
		}

		ans = k;
		calc(0, rests, 0);
		cout << ans << endl;
	}

	return EXIT_SUCCESS;
}

