using namespace std;
using ll = long long;
ll N;
ll after;
constexpr ll D[] = {1LL, 10LL, 100LL, 1000LL, 10000LL, 100000LL, 1000000LL, 10000000LL, 100000000LL, 1000000000LL};
inline ll get(const ll v, const ll d)
{
    return v / D[d] % 10;
}
ll dfs(const ll d, const ll dial)
{
    if (d == N) {
        return after == dial ? 0 : 11;
    } else {
        const ll plus = (get(after, d) - get(dial, d) + 10) % 10;
        if (plus == 0) {
            return dfs(d + 1, dial);
        } else {
            ll ans = 11;
            for (ll i = 0; i + d < N; i++) {
                ll next = dial;
                for (ll j = d; j <= d + i; j++) {
                    const ll from = get(dial, j);
                    if (from + plus < 10) {
                        next += D[j] * plus;
                    } else {
                        next -= D[j] * (10 - plus);
                    }
                }
                ans = min(ans, 1 + dfs(d + 1, next));
            }
            return ans;
        }
    }
}

int main()
{
    while (true) {
        cin >> N;
        if (N == 0) {
            break;
        }
        ll before;
        cin >> before >> after;
        cout << dfs(0, before) << endl;
    }
    return 0;
}

