  if(idx==n)return 0;

  int res=INF;
  int add=(t[idx]-s[idx]+10)%10;
  char tmp[10];
  repl(i,idx,n)tmp[i]=s[i];
  repl(i,idx,n){
    s[i]=(char)('0'+(s[i]-'0'+add)%10);
    minch(res,dfs(idx)+1);
  }
  repl(i,idx,n)s[i]=tmp[i];
  return res;
}

