int K; 
int dfs(vector<int> a, int d, int pos) {
    if(pos == K) return d;
    if(a[pos] == 0) return dfs(a, d, pos + 1);
    int res = INF;
    vector<int> temp = a; temp[pos] = 0;
    REP(i, pos + 1, K) {
        temp[i] -= a[pos];
        if(temp[i] < 0) temp[i] += 10;
        res = min(res, dfs(temp, d + 1, pos + 1));
    }
    return res;
}
