int n, a, d;
string s, t;

void calc(int p, int c){
  if(a <= c) return;
  if(p == n){
    a = min(a, c);
    return;
  }
  if(s[p] == t[p]){
    calc(p+1, c);
    return;
  }
  d = t[p] - s[p];
  for(int i=p;i<n;i++){
    for(int j=p;j<=i;j++) s[j] = ((s[j] - '0') + d + 10) % 10 + '0';
    calc(p+1, c+1);
    for(int j=p;j<=i;j++) s[j] = ((s[j] - '0') - d + 10) % 10 + '0';
  }
}

main(){
  while(cin >> n && n){
    cin >> s >> t;
    a = n;
    calc(0, 0);
    cout << a << endl;
  }
}

