int K;
int in[10], to[10];
void rot(int& x, int r){
  x = (x + r + 10) % 10;
}

int dfs(int idx){
  if(idx >= K)
	return 0;

  int diff = to[idx] - in[idx];
  if(diff == 0) return dfs(idx+1);

  int res = K;
  int prv[10];
  REP(i,K) prv[i] = in[i];
  for(int r=idx;r<K;++r){
	rot(in[r], diff);
	res = min(res, 1+dfs(idx+1));
  }
  REP(i,K) in[i] = prv[i];

  return res;
}

