		res = min( res, k );
		return;
	}
	if ( res <= k || k + 1 == K )
	{
		return;
	}

	REP( l, K )
	{
		REP( d, 10 )
		{
			REP( r, l, K )
			{
				a[r] = ( a[r] + d ) % 10;
				dfs( a, b, k + 1 );
			}
			REP( r, l, K )
			{
				a[r] = ( a[r] + 10 - d ) % 10;
			}
		}
	}

	return;
}

