		VI a, b;
		transform( ALL( S ), BI( a ), bind( minus< int >(), _1, '0' ) );
		transform( ALL( T ), BI( b ), bind( minus< int >(), _1, '0' ) );

		res = K;
		dfs( a, b, 0 );
		cout << res << endl;
	}

	return 0;
}

