int num(ll n, int i)
{
	ll mod = pow(10, i+1);
	return n%mod / (mod / 10);
}

ll add(ll n, int i, int p)
{
	int x = num(n, i);
	if (x + p >= 10) return n - pow(10, i)*(10 - p);
	else return n + pow(10, i)*p;
}

int dif(int c, int d)
{
	if (c <= d) return d - c;
	else return 10 - (c - d);
}

