int compute_turn_count(int src,int dst){
  for(int i=0;i<10;i++){
    if((src + i) % 10 == dst) return i;
  }
  return -1;
}

bool is_equal(const string& current_state,const string& unlocked_state,int first,int last){
  bool appear[10];
  memset(appear,false,sizeof(appear));

  int count = 0;
  for(int i=first;i<last;i++){
    int turn = compute_turn_count(current_state[i]-'0',unlocked_state[i]-'0');
    if(!appear[turn] ){
      appear[turn] = true;
      count++;
    }
  }
  return (count <= 1);
}

string turn(const string& str,int first,int last,int turn_count){
  string next = str;
  for(int i=first;i<=last;i++){
    next[i] = ((str[i] - '0' + turn_count) % 10) + '0';
  }
  return next;
}

