      if ( data[i] != tar[i])return false;
    }

    return true;
  }

  bool move(set<state>&S,queue<state> &Q,vector<int> &tar){
    while(now < n && data[now] == tar[now])now++;
    if ( now == n){
      if(  solved(tar))return true;
      else return false;
