  }
  if (d[p] == 0){
    return dfs(d, p + 1);
  }
  int add = 10 - d[p];
  int n = d.size();
  int ans = INF;
  for (int i = p + 1; i <= n; i++){
    vector<int> d2 = d;
    for (int j = p; j < i; j++){
      d2[j] += add;
      d2[j] %= 10;
    }
    ans = min(ans, dfs(d2, p + 1) + 1);
  }
  return ans;
}
